SELECT DISTINCT r.seq, r.ts, r.id, r.latitude, r.longitude, r.image FROM reports r INNER JOIN report_analysis ra ON r.seq = ra.seq LEFT JOIN report_status rs ON r.seq = rs.seq LEFT JOIN reports_owners ro ON r.seq = ro.seq WHERE r.id = '0x163661e12f29B7A75623AE616F72D46f7C5728D0' AND (rs.status IS NULL OR rs.status = 'active') AND ra.is_valid = TRUE AND (ra.hazard_probability >= 0.5 OR ra.litter_probability >= 0.5 OR ra.classification = 'digital') AND ra.classification = ? AND (ro.owner IS NULL OR ro.owner = '' OR ro.is_public = TRUE) ORDER BY r.seq DESC LIMIT 10;