SELECT DISTINCT r.seq, r.ts, r.id, r.latitude, r.longitude FROM reports r INNER JOIN report_analysis ra ON r.seq = ra.seq LEFT JOIN report_status rs ON r.seq = rs.seq LEFT JOIN reports_owners ro ON r.seq = ro.seq WHERE (rs.status IS NULL OR rs.status = 'active') AND ra.is_valid = TRUE AND (ro.owner IS NULL OR ro.owner = '' OR ro.is_public = TRUE) AND MATCH(ra.title, ra.description, ra.brand_name, ra.brand_display_name, ra.summary) AGAINST ('+coca-cola' IN BOOLEAN MODE) ORDER BY r.seq DESC;